import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{e}from"./app.5ce5af5d.js";const h={},r=e('<h1 id="\u7CFB\u7EDF\u8BBE\u8BA1\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#\u7CFB\u7EDF\u8BBE\u8BA1\u57FA\u7840" aria-hidden="true">#</a> \u7CFB\u7EDF\u8BBE\u8BA1\u57FA\u7840</h1><ul><li><a href="#%E7%B3%BB%E7%BB%9F%E8%AE%BE%E8%AE%A1%E5%9F%BA%E7%A1%80">\u7CFB\u7EDF\u8BBE\u8BA1\u57FA\u7840</a><ul><li><a href="#%E4%B8%80%E6%80%A7%E8%83%BD">\u4E00\u3001\u6027\u80FD</a></li><li><a href="#%E4%BA%8C%E4%BC%B8%E7%BC%A9%E6%80%A7">\u4E8C\u3001\u4F38\u7F29\u6027</a></li><li><a href="#%E4%B8%89%E6%89%A9%E5%B1%95%E6%80%A7">\u4E09\u3001\u6269\u5C55\u6027</a></li><li><a href="#%E5%9B%9B%E5%8F%AF%E7%94%A8%E6%80%A7">\u56DB\u3001\u53EF\u7528\u6027</a></li><li><a href="#%E4%BA%94%E5%AE%89%E5%85%A8%E6%80%A7">\u4E94\u3001\u5B89\u5168\u6027</a></li><li><a href="#%E5%8F%82%E8%80%83%E8%B5%84%E6%96%99">\u53C2\u8003\u8D44\u6599</a></li></ul></li></ul><h2 id="\u4E00\u3001\u6027\u80FD" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u6027\u80FD" aria-hidden="true">#</a> \u4E00\u3001\u6027\u80FD</h2><h3 id="\u6027\u80FD\u6307\u6807" tabindex="-1"><a class="header-anchor" href="#\u6027\u80FD\u6307\u6807" aria-hidden="true">#</a> \u6027\u80FD\u6307\u6807</h3><h4 id="_1-\u54CD\u5E94\u65F6\u95F4" tabindex="-1"><a class="header-anchor" href="#_1-\u54CD\u5E94\u65F6\u95F4" aria-hidden="true">#</a> 1. \u54CD\u5E94\u65F6\u95F4</h4><p>\u6307\u67D0\u4E2A\u8BF7\u6C42\u4ECE\u53D1\u51FA\u5230\u63A5\u6536\u5230\u54CD\u5E94\u6D88\u8017\u7684\u65F6\u95F4\u3002</p><p>\u5728\u5BF9\u54CD\u5E94\u65F6\u95F4\u8FDB\u884C\u6D4B\u8BD5\u65F6\uFF0C\u901A\u5E38\u91C7\u7528\u91CD\u590D\u8BF7\u6C42\u7684\u65B9\u5F0F\uFF0C\u7136\u540E\u8BA1\u7B97\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u3002</p><h4 id="_2-\u541E\u5410\u91CF" tabindex="-1"><a class="header-anchor" href="#_2-\u541E\u5410\u91CF" aria-hidden="true">#</a> 2. \u541E\u5410\u91CF</h4><p>\u6307\u7CFB\u7EDF\u5728\u5355\u4F4D\u65F6\u95F4\u5185\u53EF\u4EE5\u5904\u7406\u7684\u8BF7\u6C42\u6570\u91CF\uFF0C\u901A\u5E38\u4F7F\u7528\u6BCF\u79D2\u7684\u8BF7\u6C42\u6570\u6765\u8861\u91CF\u3002</p><h4 id="_3-\u5E76\u53D1\u7528\u6237\u6570" tabindex="-1"><a class="header-anchor" href="#_3-\u5E76\u53D1\u7528\u6237\u6570" aria-hidden="true">#</a> 3. \u5E76\u53D1\u7528\u6237\u6570</h4><p>\u6307\u7CFB\u7EDF\u80FD\u540C\u65F6\u5904\u7406\u7684\u5E76\u53D1\u7528\u6237\u8BF7\u6C42\u6570\u91CF\u3002</p><p>\u5728\u6CA1\u6709\u5E76\u53D1\u5B58\u5728\u7684\u7CFB\u7EDF\u4E2D\uFF0C\u8BF7\u6C42\u88AB\u987A\u5E8F\u6267\u884C\uFF0C\u6B64\u65F6\u54CD\u5E94\u65F6\u95F4\u4E3A\u541E\u5410\u91CF\u7684\u5012\u6570\u3002\u4F8B\u5982\u7CFB\u7EDF\u652F\u6301\u7684\u541E\u5410\u91CF\u4E3A 100 req/s\uFF0C\u90A3\u4E48\u5E73\u5747\u54CD\u5E94\u65F6\u95F4\u5E94\u8BE5\u4E3A 0.01s\u3002</p><p>\u76EE\u524D\u7684\u5927\u578B\u7CFB\u7EDF\u90FD\u652F\u6301\u591A\u7EBF\u7A0B\u6765\u5904\u7406\u5E76\u53D1\u8BF7\u6C42\uFF0C\u591A\u7EBF\u7A0B\u80FD\u591F\u63D0\u9AD8\u541E\u5410\u91CF\u4EE5\u53CA\u7F29\u77ED\u54CD\u5E94\u65F6\u95F4\uFF0C\u4E3B\u8981\u6709\u4E24\u4E2A\u539F\u56E0\uFF1A</p><ul><li>\u591A CPU</li><li>IO \u7B49\u5F85\u65F6\u95F4</li></ul><p>\u4F7F\u7528 IO \u591A\u8DEF\u590D\u7528\u7B49\u65B9\u5F0F\uFF0C\u7CFB\u7EDF\u5728\u7B49\u5F85\u4E00\u4E2A IO \u64CD\u4F5C\u5B8C\u6210\u7684\u8FD9\u6BB5\u65F6\u95F4\u5185\u4E0D\u9700\u8981\u88AB\u963B\u585E\uFF0C\u53EF\u4EE5\u53BB\u5904\u7406\u5176\u5B83\u8BF7\u6C42\u3002\u901A\u8FC7\u5C06\u8FD9\u4E2A\u7B49\u5F85\u65F6\u95F4\u5229\u7528\u8D77\u6765\uFF0C\u4F7F\u5F97 CPU \u5229\u7528\u7387\u5927\u5927\u63D0\u9AD8\u3002</p><p>\u5E76\u53D1\u7528\u6237\u6570\u4E0D\u662F\u8D8A\u9AD8\u8D8A\u597D\uFF0C\u56E0\u4E3A\u5982\u679C\u5E76\u53D1\u7528\u6237\u6570\u592A\u9AD8\uFF0C\u7CFB\u7EDF\u6765\u4E0D\u53CA\u5904\u7406\u8FD9\u4E48\u591A\u7684\u8BF7\u6C42\uFF0C\u4F1A\u4F7F\u5F97\u8FC7\u591A\u7684\u8BF7\u6C42\u9700\u8981\u7B49\u5F85\uFF0C\u90A3\u4E48\u54CD\u5E94\u65F6\u95F4\u5C31\u4F1A\u5927\u5927\u63D0\u9AD8\u3002</p><h3 id="\u6027\u80FD\u4F18\u5316" tabindex="-1"><a class="header-anchor" href="#\u6027\u80FD\u4F18\u5316" aria-hidden="true">#</a> \u6027\u80FD\u4F18\u5316</h3><h4 id="_1-\u96C6\u7FA4" tabindex="-1"><a class="header-anchor" href="#_1-\u96C6\u7FA4" aria-hidden="true">#</a> 1. \u96C6\u7FA4</h4><p>\u5C06\u591A\u53F0\u670D\u52A1\u5668\u7EC4\u6210\u96C6\u7FA4\uFF0C\u4F7F\u7528\u8D1F\u8F7D\u5747\u8861\u5C06\u8BF7\u6C42\u8F6C\u53D1\u5230\u96C6\u7FA4\u4E2D\uFF0C\u907F\u514D\u5355\u4E00\u670D\u52A1\u5668\u7684\u8D1F\u8F7D\u538B\u529B\u8FC7\u5927\u5BFC\u81F4\u6027\u80FD\u964D\u4F4E\u3002</p><h4 id="_2-\u7F13\u5B58" tabindex="-1"><a class="header-anchor" href="#_2-\u7F13\u5B58" aria-hidden="true">#</a> 2. \u7F13\u5B58</h4><p>\u7F13\u5B58\u80FD\u591F\u63D0\u9AD8\u6027\u80FD\u7684\u539F\u56E0\u5982\u4E0B\uFF1A</p><ul><li>\u7F13\u5B58\u6570\u636E\u901A\u5E38\u4F4D\u4E8E\u5185\u5B58\u7B49\u4ECB\u8D28\u4E2D\uFF0C\u8FD9\u79CD\u4ECB\u8D28\u5BF9\u4E8E\u8BFB\u64CD\u4F5C\u7279\u522B\u5FEB\uFF1B</li><li>\u7F13\u5B58\u6570\u636E\u53EF\u4EE5\u4F4D\u4E8E\u9760\u8FD1\u7528\u6237\u7684\u5730\u7406\u4F4D\u7F6E\u4E0A\uFF1B</li><li>\u53EF\u4EE5\u5C06\u8BA1\u7B97\u7ED3\u679C\u8FDB\u884C\u7F13\u5B58\uFF0C\u4ECE\u800C\u907F\u514D\u91CD\u590D\u8BA1\u7B97\u3002</li></ul><h4 id="_3-\u5F02\u6B65" tabindex="-1"><a class="header-anchor" href="#_3-\u5F02\u6B65" aria-hidden="true">#</a> 3. \u5F02\u6B65</h4><p>\u67D0\u4E9B\u6D41\u7A0B\u53EF\u4EE5\u5C06\u64CD\u4F5C\u8F6C\u6362\u4E3A\u6D88\u606F\uFF0C\u5C06\u6D88\u606F\u53D1\u9001\u5230\u6D88\u606F\u961F\u5217\u4E4B\u540E\u7ACB\u5373\u8FD4\u56DE\uFF0C\u4E4B\u540E\u8FD9\u4E2A\u64CD\u4F5C\u4F1A\u88AB\u5F02\u6B65\u5904\u7406\u3002</p><h2 id="\u4E8C\u3001\u4F38\u7F29\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u4F38\u7F29\u6027" aria-hidden="true">#</a> \u4E8C\u3001\u4F38\u7F29\u6027</h2><p>\u6307\u4E0D\u65AD\u5411\u96C6\u7FA4\u4E2D\u6DFB\u52A0\u670D\u52A1\u5668\u6765\u7F13\u89E3\u4E0D\u65AD\u4E0A\u5347\u7684\u7528\u6237\u5E76\u53D1\u8BBF\u95EE\u538B\u529B\u548C\u4E0D\u65AD\u589E\u957F\u7684\u6570\u636E\u5B58\u50A8\u9700\u6C42\u3002</p><h3 id="\u4F38\u7F29\u6027\u4E0E\u6027\u80FD" tabindex="-1"><a class="header-anchor" href="#\u4F38\u7F29\u6027\u4E0E\u6027\u80FD" aria-hidden="true">#</a> \u4F38\u7F29\u6027\u4E0E\u6027\u80FD</h3><p>\u5982\u679C\u7CFB\u7EDF\u5B58\u5728\u6027\u80FD\u95EE\u9898\uFF0C\u90A3\u4E48\u5355\u4E2A\u7528\u6237\u7684\u8BF7\u6C42\u603B\u662F\u5F88\u6162\u7684\uFF1B</p><p>\u5982\u679C\u7CFB\u7EDF\u5B58\u5728\u4F38\u7F29\u6027\u95EE\u9898\uFF0C\u90A3\u4E48\u5355\u4E2A\u7528\u6237\u7684\u8BF7\u6C42\u53EF\u80FD\u4F1A\u5F88\u5FEB\uFF0C\u4F46\u662F\u5728\u5E76\u53D1\u6570\u5F88\u9AD8\u7684\u60C5\u51B5\u4E0B\u7CFB\u7EDF\u4F1A\u5F88\u6162\u3002</p><h3 id="\u5B9E\u73B0\u4F38\u7F29\u6027" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u73B0\u4F38\u7F29\u6027" aria-hidden="true">#</a> \u5B9E\u73B0\u4F38\u7F29\u6027</h3><p>\u5E94\u7528\u670D\u52A1\u5668\u53EA\u8981\u4E0D\u5177\u6709\u72B6\u6001\uFF0C\u90A3\u4E48\u5C31\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u901A\u8FC7\u8D1F\u8F7D\u5747\u8861\u5668\u5411\u96C6\u7FA4\u4E2D\u6DFB\u52A0\u65B0\u7684\u670D\u52A1\u5668\u3002</p><p>\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u4F38\u7F29\u6027\u901A\u8FC7 Sharding \u6765\u5B9E\u73B0\uFF0C\u5C06\u6570\u636E\u6309\u4E00\u5B9A\u7684\u89C4\u5219\u5206\u5E03\u5230\u4E0D\u540C\u7684\u8282\u70B9\u4E0A\uFF0C\u4ECE\u800C\u89E3\u51B3\u5355\u53F0\u5B58\u50A8\u670D\u52A1\u5668\u7684\u5B58\u50A8\u7A7A\u95F4\u9650\u5236\u3002</p><p>\u5BF9\u4E8E\u975E\u5173\u7CFB\u578B\u6570\u636E\u5E93\uFF0C\u5B83\u4EEC\u5929\u751F\u5C31\u662F\u4E3A\u6D77\u91CF\u6570\u636E\u800C\u8BDE\u751F\uFF0C\u5BF9\u4F38\u7F29\u6027\u7684\u652F\u6301\u7279\u522B\u597D\u3002</p><h2 id="\u4E09\u3001\u6269\u5C55\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u6269\u5C55\u6027" aria-hidden="true">#</a> \u4E09\u3001\u6269\u5C55\u6027</h2><p>\u6307\u7684\u662F\u6DFB\u52A0\u65B0\u529F\u80FD\u65F6\u5BF9\u73B0\u6709\u7CFB\u7EDF\u7684\u5176\u5B83\u5E94\u7528\u65E0\u5F71\u54CD\uFF0C\u8FD9\u5C31\u8981\u6C42\u4E0D\u540C\u5E94\u7528\u5177\u5907\u4F4E\u8026\u5408\u7684\u7279\u70B9\u3002</p><p>\u5B9E\u73B0\u53EF\u6269\u5C55\u4E3B\u8981\u6709\u4E24\u79CD\u65B9\u5F0F\uFF1A</p><ul><li>\u4F7F\u7528\u6D88\u606F\u961F\u5217\u8FDB\u884C\u89E3\u8026\uFF0C\u5E94\u7528\u4E4B\u95F4\u901A\u8FC7\u6D88\u606F\u4F20\u9012\u8FDB\u884C\u901A\u4FE1\uFF1B</li><li>\u4F7F\u7528\u5206\u5E03\u5F0F\u670D\u52A1\u5C06\u4E1A\u52A1\u548C\u53EF\u590D\u7528\u7684\u670D\u52A1\u5206\u79BB\u5F00\u6765\uFF0C\u4E1A\u52A1\u4F7F\u7528\u5206\u5E03\u5F0F\u670D\u52A1\u6846\u67B6\u8C03\u7528\u53EF\u590D\u7528\u7684\u670D\u52A1\u3002\u65B0\u589E\u7684\u4EA7\u54C1\u53EF\u4EE5\u901A\u8FC7\u8C03\u7528\u53EF\u590D\u7528\u7684\u670D\u52A1\u6765\u5B9E\u73B0\u4E1A\u52A1\u903B\u8F91\uFF0C\u5BF9\u5176\u5B83\u4EA7\u54C1\u6CA1\u6709\u5F71\u54CD\u3002</li></ul><h2 id="\u56DB\u3001\u53EF\u7528\u6027" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u53EF\u7528\u6027" aria-hidden="true">#</a> \u56DB\u3001\u53EF\u7528\u6027</h2><h3 id="\u5197\u4F59" tabindex="-1"><a class="header-anchor" href="#\u5197\u4F59" aria-hidden="true">#</a> \u5197\u4F59</h3><p>\u4FDD\u8BC1\u9AD8\u53EF\u7528\u7684\u4E3B\u8981\u624B\u6BB5\u662F\u4F7F\u7528\u5197\u4F59\uFF0C\u5F53\u67D0\u4E2A\u670D\u52A1\u5668\u6545\u969C\u65F6\u5C31\u8BF7\u6C42\u5176\u5B83\u670D\u52A1\u5668\u3002</p><p>\u5E94\u7528\u670D\u52A1\u5668\u7684\u5197\u4F59\u6BD4\u8F83\u5BB9\u6613\u5B9E\u73B0\uFF0C\u53EA\u8981\u4FDD\u8BC1\u5E94\u7528\u670D\u52A1\u5668\u4E0D\u5177\u6709\u72B6\u6001\uFF0C\u90A3\u4E48\u67D0\u4E2A\u5E94\u7528\u670D\u52A1\u5668\u6545\u969C\u65F6\uFF0C\u8D1F\u8F7D\u5747\u8861\u5668\u5C06\u8BE5\u5E94\u7528\u670D\u52A1\u5668\u539F\u5148\u7684\u7528\u6237\u8BF7\u6C42\u8F6C\u53D1\u5230\u53E6\u4E00\u4E2A\u5E94\u7528\u670D\u52A1\u5668\u4E0A\uFF0C\u4E0D\u4F1A\u5BF9\u7528\u6237\u6709\u4EFB\u4F55\u5F71\u54CD\u3002</p><p>\u5B58\u50A8\u670D\u52A1\u5668\u7684\u5197\u4F59\u9700\u8981\u4F7F\u7528\u4E3B\u4ECE\u590D\u5236\u6765\u5B9E\u73B0\uFF0C\u5F53\u4E3B\u670D\u52A1\u5668\u6545\u969C\u65F6\uFF0C\u9700\u8981\u63D0\u5347\u4ECE\u670D\u52A1\u5668\u4E3A\u4E3B\u670D\u52A1\u5668\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u79F0\u4E3A\u5207\u6362\u3002</p><h3 id="\u76D1\u63A7" tabindex="-1"><a class="header-anchor" href="#\u76D1\u63A7" aria-hidden="true">#</a> \u76D1\u63A7</h3><p>\u5BF9 CPU\u3001\u5185\u5B58\u3001\u78C1\u76D8\u3001\u7F51\u7EDC\u7B49\u7CFB\u7EDF\u8D1F\u8F7D\u4FE1\u606F\u8FDB\u884C\u76D1\u63A7\uFF0C\u5F53\u67D0\u4E2A\u4FE1\u606F\u8FBE\u5230\u4E00\u5B9A\u9608\u503C\u65F6\u901A\u77E5\u8FD0\u7EF4\u4EBA\u5458\uFF0C\u4ECE\u800C\u5728\u7CFB\u7EDF\u53D1\u751F\u6545\u969C\u4E4B\u524D\u53CA\u65F6\u53D1\u73B0\u95EE\u9898\u3002</p><h3 id="\u670D\u52A1\u964D\u7EA7" tabindex="-1"><a class="header-anchor" href="#\u670D\u52A1\u964D\u7EA7" aria-hidden="true">#</a> \u670D\u52A1\u964D\u7EA7</h3><p>\u670D\u52A1\u964D\u7EA7\u662F\u7CFB\u7EDF\u4E3A\u4E86\u5E94\u5BF9\u5927\u91CF\u7684\u8BF7\u6C42\uFF0C\u4E3B\u52A8\u5173\u95ED\u90E8\u5206\u529F\u80FD\uFF0C\u4ECE\u800C\u4FDD\u8BC1\u6838\u5FC3\u529F\u80FD\u53EF\u7528\u3002</p><h2 id="\u4E94\u3001\u5B89\u5168\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001\u5B89\u5168\u6027" aria-hidden="true">#</a> \u4E94\u3001\u5B89\u5168\u6027</h2><p>\u8981\u6C42\u7CFB\u7EDF\u5728\u5E94\u5BF9\u5404\u79CD\u653B\u51FB\u624B\u6BB5\u65F6\u80FD\u591F\u6709\u53EF\u9760\u7684\u5E94\u5BF9\u63AA\u65BD\u3002</p><h2 id="\u53C2\u8003\u8D44\u6599" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u8D44\u6599" aria-hidden="true">#</a> \u53C2\u8003\u8D44\u6599</h2><ul><li>\u5927\u578B\u7F51\u7AD9\u6280\u672F\u67B6\u6784\uFF1A\u6838\u5FC3\u539F\u7406\u4E0E\u6848\u4F8B\u5206\u6790</li></ul>',50);function i(d,n){return r}var p=a(h,[["render",i],["__file","\u7CFB\u7EDF\u8BBE\u8BA1\u57FA\u7840.html.vue"]]);export{p as default};
